coding = [{'code': 'authserver', 'display': 'Authorization Server', 'definition': "An entity providing authorization services to enable the electronic sharing of health-related information based on resource owner's preapproved permissions. For example, an UMA Authorization Server[UMA]"}, {'code': 'datacollector', 'display': 'Data Collector', 'definition': "An entity that collects information over which the data subject may have certain rights under policy or law to control that information's management and distribution by data collectors, including the right to access, retrieve, distribute, or delete that information."}, {'code': 'dataprocessor', 'display': 'Data Processor', 'definition': "An entity that processes collected information over which the data subject may have certain rights under policy or law to control that information's management and distribution by data processors, including the right to access, retrieve, distribute, or delete that information."}, {'code': 'datasubject', 'display': 'Data Subject', 'definition': "A person whose personal information is collected or processed, and who may have certain rights under policy or law to control that information's management and distribution by data collectors or processors, including the right to access, retrieve, distribute, or delete that information."}, {'code': 'humanuser', 'display': 'Human User', 'definition': 'The human user that has participated.'}]
